import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import DataArray from './DataArray';
import Card from './Card';

const App = () => {
    const [search,setSearch]= useState("");
    const [list,setList] = useState(DataArray);
    const value = (data)=>{
        setSearch(data);
    }
    useEffect(()=>{
        console.log(search)
        list[0].name.includes(search);
        const newList = list.filter((current)=>{
            return current.name.includes(search);
        })
        console.log(newList);
    },[search]);
    return <>
        <Nav onClick={value} />
        <div className="container">
        {list.map((current) => 
            <Card
                key={current.id}
                data={current}
            />)
        }
    </div>
    </>;
} 
export default App;
