import React, { useState } from 'react';
import Container from './Container';
import Nav from './Nav';
const App = () => {
    const [search,setSearch]= useState("");
    const value = (data)=>{
        setSearch(data);
    }
    return <>
        <Nav onClick={value} />
        <Container filter = {search}/>
        
    </>;
} 
export default App;
