import React, { useState } from 'react';
import Container from './Container';
import Nav from './Nav';
import DataArray from './DataArray';
import Card from './Card';
const App = () => {
    const [search,setSearch]= useState("");
    const [list,setList] = useState(DataArray);
    const value = (data)=>{
        setSearch(data);
    }
    console.log(search);
    return <>
        <Nav onClick={value} />
        {/* <Container value={search}/> */}
        <div className="container">
        {list.map((current) => 
            <Card
                key={current.id}
                data={current}
            />)
        }
    </div>
    </>;
} 
export default App;
