import React, { useEffect, useState } from 'react';
import Nav from './Nav';
import DataArray from './DataArray';
import Card from './Card';

const App = () => {
    const [search,setSearch]= useState("");
    const [list,setList] = useState(DataArray);
    const value = (data)=>{
        setSearch(data);
    }
    useEffect(()=>{
        console.log(search)
        if(search===""){
            setList(DataArray);
            console.log("hello");
        }
        else{
            // setList(DataArray);
            console.log(list)
            const newList = list.filter((current)=>{
                return current.name.toLowerCase().includes(search.toLowerCase());
            })
            console.log(newList);
            setList(newList)
        }
    },[search]);
    return <>
        <Nav onClick={value} />
        <div className="container">
        {list.map((current) => 
            <Card
                key={current.id}
                data={current}
            />)
        }
        {setList(DataArray)}
    </div>
    </>;
} 
export default App;
